import '../styles/HeaderComponent.css';
import {useState} from 'react';
import { MdMenu, MdClose } from 'react-icons/md';
const HeaderComponent = ()=>{
    const [displayMenu,setDisplayMenu] = useState(true);
    return(
        <header className={displayMenu===false?"expanded-header":null}>
            <div className={displayMenu===false?"container expanded-container header-container":"container header-container"}>
                <h1>Adventure<span>Blog</span></h1>
                {displayMenu === true?
                    <MdMenu size={30} className="menu-icon" onClick={()=>setDisplayMenu(false)}/>
                    :
                    <MdClose size={30} className="menu-icon" onClick={()=>setDisplayMenu(true)}/>
                }
                <ul className="desktop-menu">
                    <li>Home</li>
                    <li>About us</li>
                    <li>Contact us</li>
                </ul>
                <button className="login-button">
                    Login
                </button>
                {/* {

                } */}
            </div>
        </header>
    )
}

export default HeaderComponent;
