import React, {createContext} from 'react';

export const GlobalState = React.createContext();
export const APIDataProvider = ({children}) => {
    const articles = [1,2,3,4];
    return(
        <GlobalState.Provider value={{articles}}>
            {children}
        </GlobalState.Provider>
    )
}
